import "../CSS/Expenses.css";
import React, { useState } from "react";
import ExpenseItem from "./ExpenseItems";
import ExpensesFilter from "../../ExpensesNew/JS/ExpenseFilter";

function Expenses(props) {
  
  const [enteredYear, setEnteredYear] = useState('');
  const filterByYear = (year) => {
    setEnteredYear(year)
  }
  const items = props.items;
  if(enteredYear!=''){
    console.log('tyear'+enteredYear);
    items= items.filter(item1 => item1.date.year=enteredYear);
  }
  return (
    <div className="expenses">
      <ExpensesFilter onFilterByYear={filterByYear}/>
      {items.map(item =>  <ExpenseItem title={item.title} amount={item.amount} date={item.date}/>)}
    </div>
  );
}
export default Expenses;
