import { useState } from "react/cjs/react.production.min";
import "../CSS/FormExpense.css";



const FormExpense = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    /*
    const [userInput, setUserInput] = useState({
        enteredTitle:'',
        enteredAmount:'',
        enteredDate:''
    });
    
    const titleChangedHandler  = (event) => {
        setUserInput({
            ...userInput,
            enteredTitle: event.target.value
        });
    }
    */
    const titleChangedHandler = (event) => {
        setEnteredTitle(event.target.value);
    }
    const amountChangedHandler = (event) => {
        setEnteredAmount(event.target.value);
    }
    const dateChangedHandler = (event) => {
        setEnteredDate(event.target.value);
    }
    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            id: Math.random().toString(),
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    }

  return (
      <form onSubmit={submitHandler}>
        <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label>
                <input type='text' onChange={titleChangedHandler}/>
            </div>
            <div className='new-expense__control'>
                <label>Amount</label>
                <input type='numbre' min='0.01' step='0.01' onChange={amountChangedHandler}/>
            </div>
            <div className='new-expense__control'>
                <label>Date</label>
                <input type='date' min='2019-01-01' max='2022-12-31' onChange={dateChangedHandler}/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type='submit'>Add Expense</button>
        </div>
      </form>
  );
}

export default FormExpense;
